let createServer,readFile;_5a1‍.w("http",[["createServer",["createServer"],function(v){createServer=v}]]);_5a1‍.w("fs",[["readFile",["readFile"],function(v){readFile=v}]]);


const server = createServer((req, res) => {
    switch (req.url) {
        case '/status': {
            res.writeHead(200, {
                'Content-Type': 'application/json'
            });
            res.write(JSON.stringify({
                status: 'Okay'
            }));
            res.end();
            break;
        }
        case '/sign-in': {
            const filePath = './pages/sign-in.html';
            readFile((filePath, error, file) => {
                if (error) {
                    res.writeHead(500, "Can't process HTML file.");
                    res.end();
                    return;
                }
                res.writeHead(200);
                res.write(file);
                res.end();
            })
            break;
        }
        case '/authenticate': {
            
            break;
        }
        default: {
            res.writeHead(404, 'Service not found.');
            res.write('Okay');
            res.end();
        }
           
    }
});

const PORT = process.env.PORT ? parseInt(process.env.PORT) : 8000;
const HOSTNAME = process.env.HOSTNAME || '127.0.0.1';   

server.listen(PORT, HOSTNAME, () => {
    console.log(`Server is listening at ${ HOSTNAME }:${ PORT }.`);
});